<template>
    <div
        class="audio"
        v-if="message.contentType == 2 && isAudioFile(message.url)"
    >
        <div class="head">
            <span class="file-name ellipsis">{{ message.content }}</span>
            <img
                class="loop-control"
                @click="toggleLoop"
                :src="loop[0]"
                ref="loop-control"
            />
        </div>
        <audio :src="message.url" controls ref="audio"></audio>
    </div>
</template>

<script>
import { isAudioFile } from "@/utils/common";
export default {
    name: "AudioMessage",
    props: {
        message: {},
    },
    data() {
        return {
            loop: [
                "data:image/png;base64,",
                "data:image/png;base64,",
            ],
        };
    },
    methods: {
        toggleLoop() {
            this.$refs.audio.loop = !this.$refs.audio.loop;
            this.$refs["loop-control"].src =
                this.loop[this.$refs.audio.loop ? 1 : 0];
        },
        isAudioFile(fileName) {
            return isAudioFile(fileName);
        },
    },
};
</script>

<style scoped lang="scss">
.audio {
    background-color: #50447e;
    .head {
        font-size: 20px;
        color: aliceblue;
        display: flex;
        justify-content: center;
        align-items: center;
        width: 100%;
        .file-name {
            width: 200px;
        }
        .loop-control {
            margin-left: 10px;
            width: 20px;
            height: 20px;
        }
    }
}
</style>
